// Will use for many blog pages
import Head from 'next/head';
import Link from 'next/link';
import Layout from '../../components/Layout';
import { useState } from 'react';
import { singleBlog } from '../../actions/blog';
import renderHTML from 'react-render-html';
import moment from 'moment';
import { API, DOMAIN, APP_NAME, FB_APP_ID } from '../../config';

const SingleBlog = ({ blog }) => {
        const head = () => (
            <Head>
                <title>Programming blogs | {APP_NAME}</title>
                <meta
                    name="description"
                    content="Programming blogs and tutorials on react node next vue php laravel and web developoment"
                />
                <link rel="canonical" href={`${DOMAIN}${router.pathname}`} />
                <meta property="og:title" content={`Latest web developoment tutorials | ${APP_NAME}`} />
                <meta
                    property="og:description"
                    content="Programming blogs and tutorials on react node next vue php laravel and web developoment"
                />
                <meta property="og:type" content="webiste" />
                <meta property="og:url" content={`${DOMAIN}${router.pathname}`} />
                <meta property="og:site_name" content={`${APP_NAME}`} />

                <meta property="og:image" content={`${DOMAIN}/static/images/seoblog.jpg`} />
                <meta property="og:image:secure_url" ccontent={`${DOMAIN}/static/images/seoblog.jpg`} />
                <meta property="og:image:type" content="image/jpg" />
                <meta property="fb:app_id" content={`${FB_APP_ID}`} />
            </Head>
        );
    const showBlogCategories = blog =>
        blog.categories.map((c, i) => (
            <Link key={i} href={`/categories/${c.slug}`}>
                <a className="btn btn-primary mr-1 ml-1 mt-3">{c.name}</a>
            </Link>
        ));

    const showBlogTags = blog =>
        blog.tags.map((t, i) => (
            <Link key={i} href={`/tags/${t.slug}`}>
                <a className="btn btn-outline-primary mr-1 ml-1 mt-3">{t.name}</a>
            </Link>
        ));
return <>
<Layout>
    <main>
        <article>
            <div className="container-fluid">
                <section>
                    {/* {JSON.stringify(blog)} */}
                    <div className="row" style={{marginTop: '-30px'}}>
                        <img src={`${API}/blog/photo/${blog.slug}`} alt={blog.title} 
                        className="img img-fluid" featured-image/>
                    </div>
                </section>
                <section> 
                <h1 className="display-2 pb-3">{blog.title}</h1>
                    <p className="lead mt-3 mark">
                    Written by {blog.postedBy.name} | Published {moment(blog.updatedAt).fromNow()}
                    </p>
                    <div className="pb-3">
                    {showBlogCategories(blog)}
                    {showBlogTags(blog)}
                        <br />
                        <br />
                    </div>
                </section>
            </div>
            {/* show content */}
            <div className="container">
                <section>
                    <div className="col-md-12 lead">
                      {renderHTML(blog.body)}  
                    </div>
                </section>
            </div>

            <div className="container pb-5">
                <h4 className="text-center pt-5 pb-5 h2">Related blogs</h4>
                <hr/>
                <p>show related blogs</p>
            </div>

            <div className="container pb-5">
                <hr/>
                <p>show Comments</p>
            </div>
        </article>
    </main>
</Layout>
</>
}
SingleBlog.getInitialProps = ({query}) => {
    return singleBlog(query.slug).then(data => {
        if(data.error) {
            console.log(data.error);
        } else {
            // console.log('GET INITIAL PROPS IN SINGLE BLOG', data)
            return {blog: data}
        }
    })
}

export default SingleBlog;
